package ArrayPractice;

public class ArrayPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String [] school = new String [3];
		school[0] = "Peak";
		school[1] = "to";
		school[2] = "Peak";
		
		for (int x = 0; x < 3; x++) {
			System.out.print(school[x] + " ");
		}
	}

}
